@Override
public void afterTextChanged(Editable s) {
    if (mPossiblyNotMultiline) {
        boolean found = false;
        for (int i = s.length() - 1; i >= 0; --i) {
            if (s.charAt(i) == '\n') {
                found = true;
                break;
            }
        }
        if (!found) {
            mMultiline = false;
            int pos = mEditText.getSelectionStart();
            mEditText.setInputType(mEditText.getInputType() & (~InputType.TYPE_TEXT_FLAG_MULTI_LINE));
            mEditText.setSelection(pos);
        }
    }
}

